import React, {useState} from 'react';
import ReactDOM from 'react-dom';

import './index.css';

const Bord = () => {
  const initialSqures = [
    null,null,null,
    null,null,null,
    null,null,null,
  ];
const [squares, setSqures] =  useState(initialSqures);

const handleClickEvent= (i) =>{
 //make a copy of squares state array
const newSqures= [...squares];
 //mutatte the copy
newSqures[i]='X';
 //call the setSquare function with
 setSqures(newSqures);
};
  const renderSqure = (i) =>{
    return(
      <Square value={squares[i]}
      onClickEvent={()=> handleClickEvent(i)}
      />
    );
  };
  return(
    <div style = {{
      backgroundColor : 'skyblue',
      margin:10,
      padding :20,
    }}>
      Bord
      <div className="bord-row">
         {renderSqure(0)}{renderSqure(1)}{renderSqure(2)}
       </div>
      <div className="bord-row">
        {renderSqure(3)}{renderSqure(4)}{renderSqure(5)}
      </div>
      <div className="bord-row">
        {renderSqure(6)}{renderSqure(7)}{renderSqure(8)}
      </div>
    </div>
  )
};

const Square = (props) => {
 const [value,setValue] = useState(null);


  return(
    <button class="squre"
     onClick ={props.onClickEvent}
    >
      {props.value}
    </button>
  )
};

const Game = () => {
  return (
    <div className = "game">
      Game
      <Bord/>
      </div>

  );
};

ReactDOM.render(
  <Game/>,
  document.getElementById('root')
);
